1234567891011121314151617181920212223242526272829303132333435363738394041function getReailFileType($filename){
    $file = fopen($filename, &quot;rb&quot;);//以只读 二进制方式打开
    $bin = fread($file, 2); //只读2字节
    fclose($file);
    $strInfo = @unpack(&quot;C2chars&quot;, $bin);//从二进制字符串对数据进行解包
    //&#39;C2char&#39;的意思是二进制文件类型是&#39;unsigned char&#39;转换为char类型最终返回值是一个数组。之后将数组返还给$strinfo函数。
    $typeCode = intval($strInfo[&#39;chars1&#39;].$strInfo[&#39;chars2&#39;]);//将$strinfo中的每一个元素转变为int型并且拼接输出给 $typeCode
    $fileType = &#39;&#39;;
    switch($typeCode){//switch匹配
        case 255216:
            $fileType = &#39;jpg&#39;;
            break;
        case 13780:
            $fileType = &#39;png&#39;;
            break;
        case 7173:
            $fileType = &#39;gif&#39;;
            break;
        default:
            $fileType = &#39;unknown&#39;;
        }
        return $fileType;
}

$is_upload = false;
$msg = null;
if(isset($_POST[&#39;submit&#39;])){
    $temp_file = $_FILES[&#39;upload_file&#39;][&#39;tmp_name&#39;];
    $file_type = getReailFileType($temp_file);

    if($file_type == &#39;unknown&#39;){
        $msg = &quot;文件未知，上传失败！&quot;;
    }else{
        $img_path = UPLOAD_PATH.&quot;/&quot;.rand(10, 99).date(&quot;YmdHis&quot;).&quot;.&quot;.$file_type;
        if(move_uploaded_file($temp_file,$img_path)){
            $is_upload = true;
        } else {
            $msg = &quot;上传出错！&quot;;
        }
    }
}
