t = []
for i in range(0, 39):
    for j in range(0, 44, 1):
        if i == 0:
            f = int(md5(list[j].encode()).hexdigest(), 16)
            hexed = hex(f)[2:]
            if (line[i].strip(&#39;\n&#39;) == hexed):
                flags += list[j]
                t.append(f)
