1234567891011121314function isImage($filename){
    $types = &#39;.jpeg|.png|.gif&#39;;
    if(file_exists($filename)){
        $info = getimagesize($filename);//è·å–æ–‡ä»¶ä¿¡æ¯
        $ext = image_type_to_extension($info[2]);//è¿”å›æ–‡ä»¶åç¼€
        if(stripos($types,$ext)&gt;=0){
            return $ext;
        }else{
            return false;
        }
    }else{
        return false;
    }
}
